import React, { useState, useEffect } from 'react';
import './Orders.css';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('L√ºtfen giri≈ü yapƒ±n');
        setIsLoading(false);
        return;
      }

      const response = await fetch('https://lezzetkraftapp-b9hnafgkebhhg7az.francecentral-01.azurewebsites.net/api/orders', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Sipari≈üler alƒ±namadƒ±');
      }

      const data = await response.json();
      setOrders(data);
      setIsLoading(false);
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return <div className="orders-container">Y√ºkleniyor...</div>;
  }

  if (error) {
    return <div className="orders-container">Hata: {error}</div>;
  }

  return (
    <div className="orders-container">
      <div className="orders-card">
        <h2>Sipari≈ülerim</h2>
        {orders.length === 0 ? (
          <div className="no-orders-message">
            <i className="no-orders-icon">üì¶</i>
            <p>Hen√ºz hi√ß sipari≈ü vermediniz.</p>
            <p className="no-orders-sub">Sipari≈ü vermek i√ßin men√ºm√ºze g√∂z atabilirsiniz.</p>
          </div>
        ) : (
          <div className="orders-list">
            {orders.map(order => (
              <div key={order._id} className="order-item">
                {/* Sipari≈ü detaylarƒ± burada g√∂sterilecek */}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Orders;
