import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    checkLoginStatus();
  }, []);

  const checkLoginStatus = () => {
    const token = localStorage.getItem('userToken');
    const userData = localStorage.getItem('userData');

    if (token && userData) {
      const user = JSON.parse(userData);
      setIsLoggedIn(true);
      setUsername(user.username);
    } else {
      setIsLoggedIn(false);
      setUsername('');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userToken');
    localStorage.removeItem('userData');
    setIsLoggedIn(false);
    setUsername('');
    navigate('/login');
  };

  return (
    <header className="header">
      <div className="header-content">
        <Link to="/" className="logo">
          Yemek Tasarım
        </Link>

        <nav className="nav-links">
          <Link to="/" className="nav-link">Ana Sayfa</Link>
          <Link to="/ingredients" className="nav-link">Malzemeler</Link>
          <Link to="/recipes" className="nav-link">Tarifler</Link>
        </nav>

        <div className="auth-buttons">
          {isLoggedIn ? (
            <>
              <span className="username">Hoş geldin, {username}</span>
              <Link to="/profile" className="profile-button">Profil</Link>
              <button onClick={handleLogout} className="logout-button">Çıkış Yap</button>
            </>
          ) : (
            <>
              <Link to="/login" className="login-button">Giriş Yap</Link>
              <Link to="/register" className="register-button">Üye Ol</Link>
            </>
          )}
        </div>
      </div>
    </header>
  );
}

export default Header;
